class Menu {
    static LEVEL_BEGINNER = 0;
    static LEVEL_INTERMEDIATE = 1;
    static LEVEL_EXPERT = 2;
    static LEVEL_CUSTOM = 3;

    constructor() {
        this.game = null;
        this.level = Menu.LEVEL_CUSTOM;
        this.sound = false;
        this.mark = false;
    }

    marks() {
        this.mark = !this.mark;
        this.toggle('marks', 'Marks (?)', this.mark);
    }

    setLevel(newLevel) {
        this.level = newLevel;
        this.toggle('beginner', 'Beginner', this.level === Menu.LEVEL_BEGINNER);
        this.toggle('intermediate', 'Intermediate', this.level === Menu.LEVEL_INTERMEDIATE);
        this.toggle('expert', 'Expert', this.level === Menu.LEVEL_EXPERT);
        this.toggle('custom', 'Custom...', this.level === Menu.LEVEL_CUSTOM);
        this.newGame();
    }

    toggleSound() {
        this.sound = !this.sound;
        this.toggle('sound', 'Sound', this.sound);
    }

    toggle(type, label, checked) {
        let id = 'dropdown-item-' + type;

        if (checked) {
            Windowing.displayGlyph(id, '‚òëÔ∏è ' + label);
        } else {
            Windowing.displayGlyph(id, label);
        }

    }

    newConfig() {
        let config;

        switch (menu.level) {
            case Menu.LEVEL_BEGINNER:
                config = new Config(10, 9, 9);
                break;
            case Menu.LEVEL_INTERMEDIATE:
                config = new Config(40, 16, 16);
                break;
            case Menu.LEVEL_EXPERT:
                config = new Config(99, 16, 30);
                break;
            case Menu.LEVEL_CUSTOM:
                let height = Windowing.getNumberInput('minesweeper_custom_height', 10);
                let width = Windowing.getNumberInput('minesweeper_custom_width', 9);
                let mines = Windowing.getNumberInput('minesweeper_custom_mines', 9);
                config = new Config(mines, height, width);
                break;
            default:
                throw new Error('no such level: ' + level);
        }

        return config;
    }

    newGame() {

        if (this.game === null) {
            this.boardHtml = Windowing.getGlyph('board-container');
        } else {
            this.game.stop();
            Windowing.displayGlyph('board-container', this.boardHtml);
        }

        this.game = new Game(this.newConfig());
        this.game.drawAllBlocks();
        this.game.addAndDisplayLeftFlags(0);
        Windowing.displayGlyph('smile', 'üôÇ');
        this.game.timer.displayTimerSeconds();
    }

}

class Windowing {

    static getPageElementById(id) {
        let found = null;

        if (typeof document !== 'undefined') {
            let element = document.querySelector("#" + id);

            if (typeof element !== 'undefined') {
                found = element;
            }

        }

        return found;
    }

    static getNumberInput(id, defaultValue) {
        let inputElement = Windowing.getPageElementById(id);
        let number;

        if (inputElement === null || inputElement.value === '') {
            number = defaultValue;
        } else {
            number = parseInt(inputElement.value, 10);
        }

        return number;
    }

    static getGlyph(id) {
        let element = Windowing.getPageElementById(id);
        let glyph = null;

        if (element !== null) {
            glyph = element.innerHTML;
        }

        return glyph;
    }

    static displayGlyph(id, glyph) {
        let element = Windowing.getPageElementById(id);

        if (element !== null) {
            element.innerHTML = glyph;
        }

    }
    
}

class GameSound {
    static tick = new Audio("data:audio/wav;base64,");
    static lose = new Audio("data:audio/wav;base64,");
    static win = new Audio("data:audio/wav;base64,");

    static playGameSound(type) {

        if (menu.sound) {
            GameSound.doPlayGameSound(type);
        }

    }

    static doPlayGameSound(type) {

        switch (type) {
            case 'tick':
                GameSound.tick.play();
                break;
            case 'lose':
                GameSound.lose.play();
                break;
            case 'win':
                GameSound.win.play();
                break;
            default:
                throw Error(type + ': no such sound');
        }

    }

}

class Timer {
    constructor() {
        this.timerSeconds = 0;
        this.isTimerOnAndShowed = false;
    }

    tickSeconds() {

        if (this.isTimerOnAndShowed && this.timerSeconds < 999) {
            this.timerSeconds++;
            this.displayTimerSeconds();
            GameSound.playGameSound('tick');
        }

    }

    displayTimerSeconds() {
        let largestDigit = Math.trunc(this.timerSeconds / 100);
        let secondDigit = Math.trunc(this.timerSeconds / 10);
        let lastDigit = this.timerSeconds % 10;
        Windowing.displayGlyph('largestDigit', largestDigit);
        Windowing.displayGlyph('secondDigit', secondDigit);
        Windowing.displayGlyph('lastDigit', lastDigit);
    }

    initialClick() {
        this.timerSeconds++;
        this.displayTimerSeconds();
        this.isTimerOnAndShowed = true;
        let myTimer = this;
        this.interval = setInterval(function() {
            myTimer.tickSeconds();
        }, 1000);
    }

    clearInterval() {

        if (this.interval) {
            clearInterval(this.interval);
        }

    }
}

class BoardPoint {

    constructor(column, row, index) {
        this.column = column;
        this.row = row;
        this.index = index;
    }

    toString() {
        return '(' + this.column + ', ' + this.row + ') index: ' + this.index;
    }
}

class Board {
    constructor(config) {
        this.config = config;
        // Initialize a two-dimensional array.
        this.board = new Array(config.columns * config.rows);

        for (let i = 0; i < config.columns * config.rows; i++) {
            this.board[i] = new Block(i);
        }

        for (let i = 0; i < config.mines; i++) {
            // Place a random bomb.
            let randomPoint;

            do {
                randomPoint = config.newRandomPoint();
            } while (this.board[randomPoint.index].bomb);

            this.board[randomPoint.index].bomb = true;
        }

    }

    replaceFirstNonBomb() {
        let block = this.board.find(block => !block.bomb);

        if (!block.bomb) {
            block.bomb = true;
            return true;
        }

        return false;
    };

    revealAllBombs(isWon) {
        this.board.forEach(block => block.revealBomb(isWon));
    };

    countNear(point, isCounted) {
        let count = 0;

        for (let row = point.row - 1; row <= point.row + 1; row++) {

            for (let column = point.column - 1; column <= point.column + 1; column++) {
                let neighbour = this.config.newPoint(column, row);

                if (neighbour && this.board[neighbour.index] && isCounted(this.board[neighbour.index])) {
                    count++;
                }

            }

        }

        return count;
    };

    getFlagBlocksCount(point) {
        return this.countNear(point, block => block.flag);
    };

    countNearBombs(point) {
        return this.countNear(point, block => block.bomb);
    };

    getBlock(point) {
        let block;

        if (point) {
            block = this.board[point.index];
        } else {
            block = null;
        }

        return block;
    }
}

class Block {
    constructor(index) {
        this.bomb = false;
        this.revealed = false;
        this.flag = false;
        this.blackBomb = false;
        this.bombWithX = false;
        this.index = index;
        this.nearBombsCount = -1;
        this.questionMark = false;
    }

    revealBomb(isWon) {

        if (!this.revealed) {

            if (this.bomb) {

                if (!this.flag) {

                    if (isWon) {
                        this.flag = true;
                    } else {
                        this.blackBomb = true;
                    }

                }

            } else if (this.flag) {
                this.bombWithX = true;
            }

        }

    };

    drawBlock() {
        let glyph;

        if (this.revealed) {

            if (this.bomb) {
                glyph = 'üí£';
            } else {
                let numbers = ['‚¨õ', '1Ô∏è‚É£', '2Ô∏è‚É£', '3Ô∏è‚É£', '4Ô∏è‚É£', '5Ô∏è‚É£', '6Ô∏è‚É£', '7Ô∏è‚É£', '8Ô∏è‚É£', '9Ô∏è‚É£'];
                glyph = numbers[this.nearBombsCount];
            }

        } else if (this.flag) {
            glyph = 'üö©';
        } else if (this.questionMark) {
            glyph = '‚ùî';
        } else {
            glyph = '‚¨úÔ∏è';
        }

        if (typeof document !== 'undefined') {
            let element = document.querySelector("#block_" + this.index);

            if (typeof element !== 'undefined') {
                element.innerHTML = glyph;
            }

        }

    }

}

class Game {
    constructor(config) {
        this.config = config;
        this.board = new Board(config);
        this.numberOfRevealedBlocks = 0;
        this.numberOfEmptyBlocks = config.columns * config.rows - config.mines;
        this.leftFlags = config.mines;
        this.rowsList = new Array(100);
        this.columnsList = new Array(100);
        this.timer = new Timer();
        this.finished = false;
    }

    stop() {
        this.timer.clearInterval();
    }

    addAndDisplayLeftFlags(leftFlagsToAdd) {
        this.leftFlags += leftFlagsToAdd;
        this.displayLeftFlags();
    };

    displayLeftFlags() {
        let lowDigit;
        let highNum;

        if (this.leftFlags >= 0) {
            lowDigit = Math.trunc(this.leftFlags / 100);
            highNum = this.leftFlags % 100;
        } else {
            lowDigit = '-';
            highNum = Math.abs(this.leftFlags % 100);
        }

        Windowing.displayGlyph('bigNum', lowDigit);
        Windowing.displayGlyph('midNum', Math.trunc(highNum / 10));
        Windowing.displayGlyph('lowNum', highNum % 10);
    };

    showBlockValue(point) {
        let block = this.board.getBlock(point);

        if (!block || block.revealed || block.flag) {
            return;
        }

        this.numberOfRevealedBlocks++;
        block.nearBombsCount = this.board.countNearBombs(point);
        block.revealed = true;
        block.drawBlock(point);

        if (block.nearBombsCount === 0) {
            this.rowsList[this.currentRowColumnListIndex] = point.row;
            this.columnsList[this.currentRowColumnListIndex] = point.column;
            this.currentRowColumnListIndex = (this.currentRowColumnListIndex + 1) % 100;
        }

    };

    expandEmptyBlock(point) {
        this.currentRowColumnListIndex = 1;
        this.showBlockValue(point);

        for (let i = 1; i !== this.currentRowColumnListIndex; i = (i + 1) % 100) {
            let row = this.rowsList[i];
            let column = this.columnsList[i];
            this.showBlockValue(this.config.newPoint(column - 1, row - 1));
            this.showBlockValue(this.config.newPoint(column, row - 1));
            this.showBlockValue(this.config.newPoint(column + 1, row - 1));
            this.showBlockValue(this.config.newPoint(column - 1, row));
            this.showBlockValue(this.config.newPoint(column + 1, row));
            this.showBlockValue(this.config.newPoint(column - 1, row + 1));
            this.showBlockValue(this.config.newPoint(column, row + 1));
            this.showBlockValue(this.config.newPoint(column + 1, row + 1));
        }

    };

    replaceFirstNonBomb(point) {

        if (this.board.replaceFirstNonBomb()) {
            let firstBlock = this.board.getBlock(point);
            firstBlock.bomb = false;
            this.expandEmptyBlock(point);
        }

    };

    handleBlockClick(point) {

        if (this.numberOfRevealedBlocks === 0 && this.timer.timerSeconds === 0) {
            // It's an initial click.
            GameSound.playGameSound('tick');
            this.timer.initialClick();
        }

        if (!this.finished) {
            Windowing.displayGlyph('smile', 'üôÇ');
            this.handleNormalBlockClick(point);
        }

    }

    handleNormalBlockClick(point) {
        let block = this.board.getBlock(point);

        if (block.bomb) {

            if (this.numberOfRevealedBlocks === 0) {
                this.replaceFirstNonBomb(point);
            } else {
                block.revealed = true;
                block.bombRedBackground = true;
                block.drawBlock();
                this.finishGame(false);
            }

        } else {
            this.expandEmptyBlock(point);

            if (this.numberOfRevealedBlocks === this.numberOfEmptyBlocks) {
                this.finishGame(true);
            }

        }

    };

    handleRightClick(e, point) {
        e.preventDefault();
        let block = this.board.getBlock(point);

        if (!block.revealed) {

            if (block.flag) {

                if (menu.mark) {
                    block.questionMark = true;
                }

                block.flag = false;
                this.addAndDisplayLeftFlags(1);
            } else if (block.questionMark) {
                block.questionMark = false;
            } else {
                // empty unclicked
                block.flag = true;
                this.addAndDisplayLeftFlags(-1);
            }

            block.drawBlock();

            if (block.flag && this.numberOfRevealedBlocks === this.numberOfEmptyBlocks) {
                this.finishGame(true);
            }

        }

    };

    finishGame(isWon) {
        this.timer.isTimerOnAndShowed = false;
        Windowing.displayGlyph('smile', isWon ? 'üòé' : 'üòµ');

        if (isWon && this.leftFlags !== 0) {
            this.addAndDisplayLeftFlags(-this.leftFlags);
        }

        GameSound.playGameSound(isWon ? 'win' : 'lose');
        this.finished = true;
    }

    drawAllBlocks() {

        if (typeof document === 'undefined') {
            return;
        }

        let table = document.createElement('table');
        table.style.borderSpacing = '0px';
        let body = document.querySelector('#table_container');
        body.appendChild(table)
        let tableBody = table.createTBody();

        for (let row = 0; row < this.config.rows; row++) {
            let tableRow = tableBody.insertRow(row);

            for (let column = 0; column < this.config.columns; column++) {
                let cell = tableRow.insertCell(column);
                let point = this.config.newPoint(column, row);
                cell.style.padding = '0px';
                cell.id = 'block_' + point.index;
                cell.style.cursor = 'default';
                cell.addEventListener('click', () => this.handleBlockClick(point))
                cell.addEventListener('contextmenu', (e) => this.handleRightClick(e, point))
            }

        }

        this.board.board.forEach(block => block.drawBlock());
    }
}

class Config {
    constructor(mines, rows, columns) {
        this.mines = mines;
        this.rows = rows;
        this.columns = columns;

        if (this.mines > this.rows * this.columns) {
            throw Error('too many mines');
        }

    }

    newPoint(column, row) {
        let point = null;

        if (row >= 0 && row < this.rows && column >= 0 && column < this.columns) {
            let index = row * this.columns + column;
            point = new BoardPoint(column, row, index);
        }

        return point;
    }

    newRandomPoint() {
        let column = Math.floor(this.columns * Math.random());
        let row = Math.floor(this.rows * Math.random());
        return this.newPoint(column, row);
    }

}

function runGame() {

    if (typeof menu === 'undefined') {
        menu = new Menu();
    }

    menu.newGame();
}
